import { createApi } from '@reduxjs/toolkit/query/react';
import { customFetchBase } from '.';
import { LoginInput } from '../../components/utils/types';
import { IUser, setToken, logout } from '../reducers/userSlice';

export const registrationApi = createApi({
  reducerPath: 'registrationApi',
  baseQuery: customFetchBase,
  endpoints: (builder) => ({
    registrationUser: builder.mutation<string, LoginInput>({
      query(data) {
        return {
          url: 'auth/registration',
          method: 'POST',
          body: data,
          credentials: 'include',
        };
      },
      async onQueryStarted(args, { dispatch, queryFulfilled }) {
        try {
          const { data } = await queryFulfilled;
          dispatch(setToken(data));
        } catch (error) {
          console.log(`error =>>>>>>>>>>`, error);
        }
      },
    }),
    /* logoutUser: builder.mutation<void, void>({
      query() {
        return {
          url: 'auth/logout',
          credentials: 'include',  //// useLogoutUserMutation
        };
      },
      async onQueryStarted(args, { dispatch, queryFulfilled }) {
        try {
          await queryFulfilled;
          dispatch(logout());
        } catch (error) {}
      },
    }), */
  }),
});

export const { useRegistrationUserMutation } = registrationApi;
